// Simple client configuration for single-client deployment
export interface ClientConfig {
  clientName: string;
  logoImage: string;
  backgroundImage?: string;
  primaryColor: string;
  secondaryColor: string;
  companyCode: string;
}

// Get client configuration from environment variables
export const getCurrentClientConfig = (): ClientConfig => {
  return {
    clientName: process.env.REACT_APP_CLIENT_NAME || 'Clinical Portal',
    logoImage: process.env.REACT_APP_LOGO_IMAGE || '/assets/client/logo.png',
    backgroundImage: process.env.REACT_APP_BACKGROUND_IMAGE || '/assets/client/background.jpg',
    primaryColor: process.env.REACT_APP_PRIMARY_COLOR || '#1976d2',
    secondaryColor: process.env.REACT_APP_SECONDARY_COLOR || '#dc004e',
    companyCode: process.env.REACT_APP_COMPANY_CODE || 'UBI'
  };
};
