import React from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  Grid,
  Chip,
} from '@mui/material';
import { clientConfigs } from '../../config/clientConfig';

const ClientDemo: React.FC = () => {
  const handleClientChange = (clientKey: string) => {
    const currentUrl = new URL(window.location.href);
    currentUrl.searchParams.set('client', clientKey);
    window.location.href = currentUrl.toString();
  };

  return (
    <Card sx={{ mb: 3 }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          ðŸŽ¨ Client Configuration Demo
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
          Click any client below to see the branded interface:
        </Typography>

        <Grid container spacing={2}>
          {Object.entries(clientConfigs).map(([key, config]) => (
            <Grid item xs={12} sm={6} md={3} key={key}>
              <Card 
                variant="outlined" 
                sx={{ 
                  cursor: 'pointer',
                  '&:hover': { boxShadow: 2 },
                  border: `2px solid ${config.primaryColor}20`
                }}
                onClick={() => handleClientChange(key)}
              >
                <CardContent sx={{ textAlign: 'center', py: 2 }}>
                  <Box
                    sx={{
                      width: 60,
                      height: 40,
                      mx: 'auto',
                      mb: 1,
                      bgcolor: config.primaryColor,
                      borderRadius: 1,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontSize: '0.8rem',
                      fontWeight: 'bold'
                    }}
                  >
                    LOGO
                  </Box>
                  <Typography variant="subtitle2" fontWeight="bold">
                    {config.clientName}
                  </Typography>
                  <Chip 
                    size="small" 
                    label={config.companyCode}
                    sx={{ 
                      mt: 0.5,
                      bgcolor: config.primaryColor,
                      color: 'white',
                      fontSize: '0.7rem'
                    }}
                  />
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>

        <Box sx={{ mt: 2, p: 2, bgcolor: 'background.default', borderRadius: 1 }}>
          <Typography variant="caption" color="text.secondary">
            <strong>Deployment Instructions:</strong><br/>
            â€¢ Set environment variables: REACT_APP_CLIENT_NAME, REACT_APP_LOGO_IMAGE, REACT_APP_COMPANY_CODE<br/>
            â€¢ Place client logos in /public/assets/logos/<br/>
            â€¢ Place background images in /public/assets/backgrounds/<br/>
            â€¢ Each client gets their own branded interface automatically
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default ClientDemo;
