// Simple client configuration for single-client deployment
export interface ClientConfig {
  clientName: string;
  clientLogo: string;
  myLogo: string;
  backgroundImage?: string;
  primaryColor: string;
  secondaryColor: string;
}

// Get client configuration from environment variables
export const getCurrentClientConfig = (): ClientConfig => {
  return {
    clientName: process.env.REACT_APP_CLIENT_NAME || 'Clinical Portal',
    clientLogo: '/assets/client/logo.png',
    myLogo: '/assets/logos/my-logo.svg',
    backgroundImage: process.env.REACT_APP_BACKGROUND_IMAGE,
    primaryColor: process.env.REACT_APP_PRIMARY_COLOR || '#2563eb',
    secondaryColor: process.env.REACT_APP_SECONDARY_COLOR || '#64748b'
  };
};
